import React, { Component } from 'react'
import axios from "axios"
import Link from 'react-router-dom'
import exerciseList from '../components/'
const Exercise = props => (
    <tr>
        <td>props.exercise.username</td>
        <td>props.exercise.description</td>

        <td>props.exercise.duration</td>

        <td>props.exercise.date.substring(0,10</td>

            <Link to={"/edit"+ props.exercise._id}>edit</Link> | <a herf="#" onClick={() =>{props.deleteExercise(props.exercise._id)}}>delete</a>
    </tr>
)

export default class ExercisesList extends Component {
    constructor(props){
        super(props)
        this.deleteExercise.bind(this);
        this.state = {exercises: []}
    }
    componentDidMount(){
        axios.get('http://localhost:5000/exercises/')
        .then(res => {
            this.state({exercises: res.data})
        })
        .catch((err) =>{
            console.log(err)
        })



    }
    deleteExercise(id) {
        axios.delete('http://locahost:5000/exercises/'+id)
        .then(res => console.log(res.data));

        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id)
        })
    }
    exerciseList(){
        return this.state.exercises.map(currentexercise =>{
            return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
        })
    }
    render() {
        return (
            <div>
                <h3>logged exercises</h3>
                <table>
                <thead className="thead-light">
                    <tr>
                    <th>Username</th>
                    <th>Description</th>
                    <th>Date</th>
                    <th>Actions</th>
                    </tr>


                </thead>
                <tbody>
                    {this.exerciseList()}
                </tbody>
                </table>
            </div>
        )
    }
}
